// Script to convert usecases.ts to usecases.js
// Run this script whenever you update usecases.ts

const fs = require('fs');

// Read the TypeScript file
const tsContent = fs.readFileSync('usecases.ts', 'utf8');

// Extract the additionalUsecases array from the TypeScript file
// This looks for: export const additionalUsecases: Usecase[] = [...]
const match = tsContent.match(/export const additionalUsecases[^=]*=\s*(\[[\s\S]*\]);/);

if (match && match[1]) {
    // Create the JavaScript version
    const jsContent = `// Auto-generated from usecases.ts
// Do not edit this file directly - edit usecases.ts instead

window.additionalUsecases = ${match[1]};
`;

    // Write to usecases.js
    fs.writeFileSync('usecases.js', jsContent, 'utf8');
    console.log('‚úÖ Successfully converted usecases.ts to usecases.js');

    // Count the use cases
    const usecases = eval(match[1]);
    console.log(`üìä Converted ${usecases.length} use cases`);
} else {
    console.log('‚ö†Ô∏è  No additionalUsecases array found in usecases.ts');
    console.log('Creating empty usecases.js file...');
    fs.writeFileSync('usecases.js', 'window.additionalUsecases = [];', 'utf8');
}
