// Generate blueprint from description
function generateBlueprint(description: string, tech: string[]): string {
  const steps: string[] = [];

  // Analyze description for action patterns
  if (description.toLowerCase().includes("chatbot") || description.toLowerCase().includes("virtual assistant") || description.toLowerCase().includes("conversational")) {
    steps.push("Deploy conversational AI interface");
    steps.push("Train model on domain-specific data");
    steps.push("Integrate with customer touchpoints");
    steps.push("Monitor and optimize responses");
  } else if (description.toLowerCase().includes("automat") || description.toLowerCase().includes("streamlin")) {
    steps.push("Identify manual processes to automate");
    steps.push("Implement AI-powered automation workflows");
    steps.push("Integrate with existing systems");
    steps.push("Measure efficiency gains");
  } else if (description.toLowerCase().includes("analyz") || description.toLowerCase().includes("insight") || description.toLowerCase().includes("data")) {
    steps.push("Collect and structure data sources");
    steps.push("Apply AI/ML analysis models");
    steps.push("Generate actionable insights");
    steps.push("Enable data-driven decisions");
  } else if (description.toLowerCase().includes("predict") || description.toLowerCase().includes("forecast")) {
    steps.push("Aggregate historical data");
    steps.push("Train predictive models");
    steps.push("Generate forecasts and predictions");
    steps.push("Integrate into decision workflows");
  } else if (description.toLowerCase().includes("search") || description.toLowerCase().includes("discover")) {
    steps.push("Index content and data sources");
    steps.push("Implement AI-powered search");
    steps.push("Enable natural language queries");
    steps.push("Deliver relevant results");
  } else if (description.toLowerCase().includes("generat") && (description.toLowerCase().includes("content") || description.toLowerCase().includes("image") || description.toLowerCase().includes("video"))) {
    steps.push("Define content requirements");
    steps.push("Configure generative AI models");
    steps.push("Generate and review content");
    steps.push("Deploy at scale");
  } else if (description.toLowerCase().includes("security") || description.toLowerCase().includes("fraud") || description.toLowerCase().includes("detect")) {
    steps.push("Implement security monitoring");
    steps.push("Deploy AI detection models");
    steps.push("Enable real-time alerts");
    steps.push("Automate response actions");
  } else if (description.toLowerCase().includes("code") || description.toLowerCase().includes("develop")) {
    steps.push("Integrate AI coding assistant");
    steps.push("Enable code suggestions and reviews");
    steps.push("Automate testing workflows");
    steps.push("Improve developer productivity");
  } else {
    steps.push("Assess current capabilities");
    steps.push("Implement AI solution with " + (tech[0] || "Google Cloud"));
    steps.push("Integrate with existing workflows");
    steps.push("Monitor and optimize results");
  }

  return steps.join(" -> ");
}

// Extract keywords from description and tech
function extractKeywords(description: string, tech: string[]): string[] {
  const keywords: string[] = [];
  const lowerDesc = description.toLowerCase();

  // Add relevant tech keywords
  tech.slice(0, 3).forEach(t => keywords.push(t));

  // Extract action-based keywords
  if (lowerDesc.includes("customer") || lowerDesc.includes("support")) keywords.push("Customer Experience");
  if (lowerDesc.includes("automat")) keywords.push("Automation");
  if (lowerDesc.includes("real-time") || lowerDesc.includes("realtime")) keywords.push("Real-time");
  if (lowerDesc.includes("personali")) keywords.push("Personalization");
  if (lowerDesc.includes("analytic") || lowerDesc.includes("insight")) keywords.push("Analytics");
  if (lowerDesc.includes("search")) keywords.push("Search");
  if (lowerDesc.includes("chatbot") || lowerDesc.includes("virtual assistant")) keywords.push("Conversational AI");
  if (lowerDesc.includes("security") || lowerDesc.includes("fraud")) keywords.push("Security");
  if (lowerDesc.includes("efficienc") || lowerDesc.includes("productiv")) keywords.push("Productivity");

  return [...new Set(keywords)].slice(0, 6);
}

// Process 101 use cases from JSON
const processed101: UseCase[] = (usecases101 as any[]).map((uc, index) => {
  const fullContent = [
    uc.title || "",
    uc.summary || "",
    uc.sections?.business_challenge?.content || "",
    uc.sections?.blueprint?.content || "",
    ...(uc.sections?.tech_stack?.items || [])
  ].join(" ");

  return {
    usecase_id: uc.usecase_id || index + 1,
    title: uc.title || `Use Case ${index + 1}`,
    summary: uc.summary || uc.title || "",
    sections: {
      business_challenge: uc.sections?.business_challenge || { heading: "Business Challenge", content: "" },
      tech_stack: uc.sections?.tech_stack || { heading: "Tech Stack", items: [] },
      blueprint: uc.sections?.blueprint || { heading: "Blueprint", content: "" }
    },
    category: uc.category || allCategories[index % allCategories.length],
    industry: uc.industry || allIndustries[index % allIndustries.length],
    keywords: extractKeywords(fullContent, uc.sections?.tech_stack?.items || []),
    company: uc.company
  };
});

